record.field:
def _odoo_execute_kw(secrets): import xmlrpc.client as _client if not all([params.URL, params.DB, secrets.USERNAME, secrets.PASSWORD]): raise UserError("External Odoo Credentials are not set")
def f(model, method, *args, **kwargs): # TODO: raise RetryableJobError in case of connection errors log_transmission("%s::%s" % (params.URL, params.DB), json.dumps([method, args, kwargs])) common = _client.ServerProxy('{}/xmlrpc/2/common'.format(params.URL)) uid = common.authenticate(params.DB, secrets.USERNAME, secrets.PASSWORD, {}) models = xmlrpc.client.ServerProxy('{}/xmlrpc/2/object'.format(params.URL)) res = models.execute_kw(params.DB, uid, secrets.PASSWORD, model, method, args, kwargs) log("Response: %s" % res, level="debug") return res return f
odoo_execute_kw = _odoo_execute_kw(secrets)

PARTNER_REL = "odoo2odoo_partner"
IMAGE_FIELD = "image"
def partner2vals(partner):
    FIELDS = ["name", IMAGE_FIELD]
    res = {}
    for f in FIELDS:
        res[f] = getattr(partner, f)
    return res

def parse_date(s):
    raise NotImplementedError(s)

def create_external_partners(partners, name_prefix=""):
    for partner in partners:
        vals = partner2vals(partner)
        vals["name"] = name_prefix + vals["name"]
        external_partner_id = odoo_execute_kw("res.partner", "create", vals)
        date_update = odoo_execute_kw("res.partner", "read", [external_partner_id], fields=["create_date"])
        partner.set_link(PARTNER_REL, external_partner_id, date_update)

def handle_db(records):
    # records are instances of created res.partner
    create_external_partners(records)

def handle_button():
    chunk = None
    CHUNK_SIZE = 10
    for r in env["res.partner"].search([]):
        # check that record is not created yet
        link = r.search_links(PARTNER_REL)
        if link:
            # object already exists
            continue

        if not chunk:
            chunk = r
        else:
            chunk |= r
        if len(chunk) == CHUNK_SIZE:
            call_async("create_external_partners")(chunk, name_prefix=params.UPLOAD_ALL_PARTNER_PREFIX)
            chunk = None
